import React, { useState } from 'react';
import './styles.css';

// Google Sheet Web App URL
const GOOGLE_SHEET_URL = 'https://script.google.com/macros/s/AKfycby5-eOuyEh5lm-5caMgjkXpIvd-yuMUhTJdZeheMwwwAYCKgyvyiQ3-R5_3anJ-2x5i/exec';

interface OrderFormProps {
  onClose: () => void;
  cartItems: {
    id: string;
    title: string;
    price: string;
    quantity: number;
  }[];
  onSubmit: (formData: {
    name: string;
    phone: string;
    address: string;
    deliveryTime: string;
    note?: string;
  }) => void;
}

const OrderForm: React.FC<OrderFormProps> = ({ onClose, cartItems, onSubmit }) => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    address: '',
    deliveryTime: '',
    note: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  // T√≠nh t·ªïng ti·ªÅn t·ª´ gi·ªè h√†ng
  const calculateTotal = () => {
    return cartItems.reduce((sum, item) => {
      const price = parseFloat(item.price.replace(/[^0-9.-]+/g, ''));
      return sum + (price * item.quantity);
    }, 0);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    const totalAmount = calculateTotal();
    const cartItemsString = cartItems.map(item => 
      `${item.title} (${item.quantity}x${item.price})`
    ).join(', ');

    // Chu·∫©n b·ªã d·ªØ li·ªáu ƒë·ªÉ g·ª≠i ƒë·∫øn Google Sheets
    const formDataToSubmit = new FormData();
    formDataToSubmit.append('name', formData.name);
    formDataToSubmit.append('phone', formData.phone);
    formDataToSubmit.append('address', formData.address);
    formDataToSubmit.append('deliveryTime', formData.deliveryTime);
    formDataToSubmit.append('note', formData.note || '');
    formDataToSubmit.append('cartItems', cartItemsString);
    formDataToSubmit.append('totalAmount', new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(totalAmount));

    try {
      // G·ª≠i d·ªØ li·ªáu ƒë·∫øn Google Sheets
      const response = await fetch(GOOGLE_SHEET_URL, {
        method: 'POST',
        mode: 'no-cors', // Th√™m mode no-cors ƒë·ªÉ tr√°nh l·ªói CORS
        body: formDataToSubmit
      });

      // V√¨ mode l√† no-cors, ch√∫ng ta kh√¥ng th·ªÉ ƒë·ªçc response
      // N√™n c·ª© xem nh∆∞ th√†nh c√¥ng v√† th√¥ng b√°o cho ng∆∞·ªùi d√πng
      onSubmit(formData);
      alert('ƒê∆°n h√†ng ƒë√£ ƒë∆∞·ª£c g·ª≠i th√†nh c√¥ng!');
      onClose();
    } catch (error) {
      console.error('L·ªói khi g·ª≠i ƒë∆°n h√†ng:', error);
      alert('C√≥ l·ªói x·∫£y ra khi g·ª≠i ƒë∆°n h√†ng. Vui l√≤ng th·ª≠ l·∫°i sau.');
    } finally {
      setIsSubmitting(false);
    }
  };

  // T√≠nh th·ªùi gian cho datetime-local
  const getMinDateTime = () => {
    const now = new Date();
    now.setHours(now.getHours() + 1); // √çt nh·∫•t 1 ti·∫øng sau th·ªùi ƒëi·ªÉm hi·ªán t·∫°i
    return now.toISOString().slice(0, 16); // Format YYYY-MM-DDThh:mm
  };

  const getMaxDateTime = () => {
    const now = new Date();
    now.setDate(now.getDate() + 14); // T·ªëi ƒëa 14 ng√†y sau
    return now.toISOString().slice(0, 16);
  };

  return (
    <div className="order-form">
      <div className="order-form-header">
        <h3>üìã X√°c nh·∫≠n ƒë∆°n h√†ng</h3>
        <button onClick={onClose} className="close-button" title="ƒê√≥ng">√ó</button>
      </div>

      <div className="order-form-content">
        <div className="cart-items">
          <h4 className="cart-title">Gi·ªè h√†ng c·ªßa b·∫°n</h4>
          {cartItems.map((item) => (
            <div key={item.id} className="cart-item">
              <span className="item-title">{item.title}</span>
              <span className="item-quantity">x{item.quantity}</span>
              <span className="item-price">{item.price}</span>
            </div>
          ))}
          <div className="total-amount">
            T·ªïng ti·ªÅn: {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(calculateTotal())}
          </div>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">H·ªç t√™n</label>
            <input
              type="text"
              id="name"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              placeholder="Nh·∫≠p h·ªç t√™n c·ªßa b·∫°n"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="phone">S·ªë ƒëi·ªán tho·∫°i</label>
            <input
              type="tel"
              id="phone"
              value={formData.phone}
              onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
              placeholder="Nh·∫≠p s·ªë ƒëi·ªán tho·∫°i"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="deliveryTime">Th·ªùi gian nh·∫≠n h√†ng</label>
            <input
              type="datetime-local"
              id="deliveryTime"
              value={formData.deliveryTime}
              onChange={(e) => setFormData({ ...formData, deliveryTime: e.target.value })}
              min={getMinDateTime()}
              max={getMaxDateTime()}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="address">ƒê·ªãa ch·ªâ nh·∫≠n h√†ng</label>
            <textarea
              id="address"
              value={formData.address}
              onChange={(e) => setFormData({ ...formData, address: e.target.value })}
              placeholder="Nh·∫≠p ƒë·ªãa ch·ªâ nh·∫≠n h√†ng"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="note">Ghi ch√∫ (kh√¥ng b·∫Øt bu·ªôc)</label>
            <textarea
              id="note"
              value={formData.note}
              onChange={(e) => setFormData({ ...formData, note: e.target.value })}
              placeholder="Nh·∫≠p ghi ch√∫ n·∫øu c√≥"
            />
          </div>

          <div className="form-actions">
            <button 
              type="submit" 
              className="submit-button"
              disabled={isSubmitting}
            >
              {isSubmitting ? '‚è≥ ƒêang x·ª≠ l√Ω...' : 'üí≥ ƒê·∫∑t h√†ng ngay'}
            </button>
            <button 
              type="button" 
              onClick={onClose} 
              className="cancel-button"
              disabled={isSubmitting}
            >
              ‚ùå H·ªßy
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default OrderForm;
