import React, { useState } from 'react';
import './styles.css';

interface OrderFormProps {
  onClose: () => void;
  cartItems: {
    id: string;
    title: string;
    price: string;
    quantity: number;
  }[];
}

const OrderForm: React.FC<OrderFormProps> = ({ onClose, cartItems }) => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    address: '',
    deliveryTime: '',
    note: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState<{
    type: 'success' | 'error' | null;
    message: string;
  }>({ type: null, message: '' });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSubmitStatus({ type: null, message: '' });

    // Format cart items for Google Sheets
    const cartItemsFormatted = cartItems.map(item => 
      `${item.title} (${item.quantity}x${item.price})`
    ).join(', ');

    const totalAmount = cartItems.reduce((sum, item) => {
      const price = parseFloat(item.price.replace(/[^0-9.-]+/g, ''));
      return sum + (price * item.quantity);
    }, 0);

    // Prepare data for Google Sheets
    const dataToSend = {
      timestamp: new Date().toISOString(),
      name: formData.name,
      phone: formData.phone,
      address: formData.address,
      deliveryTime: formData.deliveryTime,
      note: formData.note || 'Kh√¥ng c√≥ ghi ch√∫',
      items: cartItemsFormatted,
      totalAmount: new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(totalAmount)
    };

    try {
      const response = await fetch('https://script.google.com/macros/s/AKfycbxnA80ttY8iGSeHPUIUy7emtQ33fnTSv3HHl9UVcwMb6a5zVOX8n9d3UttWzGsCKuLJ/exec', {
        method: 'POST',
        mode: 'no-cors',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(dataToSend)
      });

      setSubmitStatus({
        type: 'success',
        message: 'ƒê∆°n h√†ng c·ªßa b·∫°n ƒë√£ ƒë∆∞·ª£c ghi nh·∫≠n th√†nh c√¥ng!'
      });

      // Clear form after successful submission
      setFormData({
        name: '',
        phone: '',
        address: '',
        deliveryTime: '',
        note: ''
      });

      // Close form after 2 seconds
      setTimeout(() => {
        onClose();
      }, 2000);

    } catch (error) {
      setSubmitStatus({
        type: 'error',
        message: 'C√≥ l·ªói x·∫£y ra khi g·ª≠i ƒë∆°n h√†ng. Vui l√≤ng th·ª≠ l·∫°i sau.'
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const totalAmount = cartItems.reduce((sum, item) => {
    const price = parseFloat(item.price.replace(/[^0-9.-]+/g, ''));
    return sum + (price * item.quantity);
  }, 0);

  // T√≠nh th·ªùi gian cho datetime-local
  const getMinDateTime = () => {
    const now = new Date();
    now.setHours(now.getHours() + 1); // √çt nh·∫•t 1 ti·∫øng sau th·ªùi ƒëi·ªÉm hi·ªán t·∫°i
    return now.toISOString().slice(0, 16); // Format YYYY-MM-DDThh:mm
  };

  const getMaxDateTime = () => {
    const now = new Date();
    now.setDate(now.getDate() + 14); // T·ªëi ƒëa 14 ng√†y sau
    return now.toISOString().slice(0, 16);
  };

  return (
    <div className="order-form">
      <div className="order-form-header">
        <h3>üìã X√°c nh·∫≠n ƒë∆°n h√†ng</h3>
        <button onClick={onClose} className="close-button" title="ƒê√≥ng">√ó</button>
      </div>

      <div className="order-form-content">
        <div className="cart-items">
          <h4 className="cart-title">Gi·ªè h√†ng c·ªßa b·∫°n</h4>
          {cartItems.map((item) => (
            <div key={item.id} className="cart-item">
              <span className="item-title">{item.title}</span>
              <span className="item-quantity">x{item.quantity}</span>
              <span className="item-price">{item.price}</span>
            </div>
          ))}
          <div className="total-amount">
            T·ªïng ti·ªÅn: {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(totalAmount)}
          </div>
        </div>

        {submitStatus.type && (
          <div className={`submit-status ${submitStatus.type}`}>
            {submitStatus.message}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">H·ªç t√™n</label>
            <input
              type="text"
              id="name"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              placeholder="Nh·∫≠p h·ªç t√™n c·ªßa b·∫°n"
              required
              disabled={isSubmitting}
            />
          </div>

          <div className="form-group">
            <label htmlFor="phone">S·ªë ƒëi·ªán tho·∫°i</label>
            <input
              type="tel"
              id="phone"
              value={formData.phone}
              onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
              placeholder="Nh·∫≠p s·ªë ƒëi·ªán tho·∫°i"
              required
              disabled={isSubmitting}
            />
          </div>

          <div className="form-group">
            <label htmlFor="deliveryTime">Th·ªùi gian nh·∫≠n h√†ng</label>
            <input
              type="datetime-local"
              id="deliveryTime"
              value={formData.deliveryTime}
              onChange={(e) => setFormData({ ...formData, deliveryTime: e.target.value })}
              min={getMinDateTime()}
              max={getMaxDateTime()}
              required
              disabled={isSubmitting}
            />
          </div>

          <div className="form-group">
            <label htmlFor="address">ƒê·ªãa ch·ªâ nh·∫≠n h√†ng</label>
            <textarea
              id="address"
              value={formData.address}
              onChange={(e) => setFormData({ ...formData, address: e.target.value })}
              placeholder="Nh·∫≠p ƒë·ªãa ch·ªâ nh·∫≠n h√†ng"
              required
              disabled={isSubmitting}
            />
          </div>

          <div className="form-group">
            <label htmlFor="note">Ghi ch√∫ (kh√¥ng b·∫Øt bu·ªôc)</label>
            <textarea
              id="note"
              value={formData.note}
              onChange={(e) => setFormData({ ...formData, note: e.target.value })}
              placeholder="Nh·∫≠p ghi ch√∫ n·∫øu c√≥"
              disabled={isSubmitting}
            />
          </div>

          <div className="form-actions">
            <button type="submit" className="submit-button" disabled={isSubmitting}>
              {isSubmitting ? '‚è≥ ƒêang x·ª≠ l√Ω...' : 'üí≥ ƒê·∫∑t h√†ng ngay'}
            </button>
            <button type="button" onClick={onClose} className="cancel-button" disabled={isSubmitting}>
              ‚ùå H·ªßy
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default OrderForm;
